pragma solidity ^0.8.28;

/**
 * @title BBTypes
 * @dev 牛牛游戏类型定义
 */

// 游戏状态
enum GameState {
    NONE,
    WAITING,
    FIRST_BETTING,
    SECOND_BETTING,
    ENDED,
    SETTLED,
    LIQUIDATED
}

// 玩家状态
enum PlayerState {
    NONE,
