// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "./BBCardUtils.sol";
import "./BBPlayer.sol";
import "./BBTypes.sol";
import "./BBCardDealer.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
// import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "./BBInterfaces.sol";

/**
 * @title BBGameTableImplementation
 * @dev 
 */
contract BBGameTableImplementation is ReentrancyGuard, Ownable {
    using BBPlayerLib for BBPlayer;
    using BBCardUtils for uint8[5];
    using BBCardDealer for BBCardDealer.DealerState;

    // 
    bool public active;
    uint256 public tableId; // ID
    string public tableName;
    address public bankerAddr;
    uint256 public betAmount;  // 
    uint8 public playerCount;
    uint8 public maxPlayers;
    uint256 public creationTimestamp;
    GameState public state;
    uint256 public rewardPoolId;
    uint256 public bankerStakeAmount;  //
    uint256 public totalIncome; // 

    // 2
    uint8 public firstBetX;
    uint8 public secondBetX;

    uint256 public gameRound; //
    uint256 public gameLiquidatedCount; //

    uint256 public finalSeed; // 

    uint256 public gameStartTimestamp;
    uint256 public gameEndTimestamp;
    uint8 public playerContinuedCount;
    uint8 public playerReadyCount;
    uint256 public totalPrizePool;  //
    uint8 public bankerFeePercent; // 
    uint8 public liquidatorFeePercent; // 
    uint256 public implementationVersion; // 

    // 
    BBPlayer[] public players;

    // 
    BBCardDealer.DealerState private dealerState;

    //
    address public gameMainAddr;
    address public rewardPoolAddr; // 
    address public roomCardAddr; // 

    // 
    uint256 public playerTimeout; // 
    uint256 public currentRoundDeadline; // 

    uint256 public tableInactiveTimeout; // 
    uint256 public lastActivityTimestamp; // 

    //
    address public rewardAddr; // 
    uint256 public rewardAmount; // 

    string public chatGroupId; // ID

    //  50  slot 
    uint256[50] private __gap;

    // 
    event GameTableChanged(address indexed tableAddr);
    event GameTableInitialized(address indexed tableAddr, address indexed banker, uint256 version);
    event CreateGameHistory(address indexed tableAddr, uint256 round, uint256 gameStartTimestamp, uint256 gameEndTimestamp, address[] playerAddrs, address[] winnerAddrs, uint256[] playerBets, uint8[5][] playerCards);


    // 
    constructor() Ownable(msg.sender){
        //  Ownable 
    }

    /**
     * @dev 
     */
    function initialize(
        uint256 _tableId,
        string memory _tableName,
        address _bankerAddr,
        uint256 _betAmount,
        uint8 _maxPlayers,
        address _gameMainAddr,
        uint8 _bankerFeePercent,
        uint8 _firstRaise,
        uint8 _secondRaise,
        uint256 _implementationVersion
    ) external {
        // 
        require(creationTimestamp == 0, "Already initialized");

        _transferOwnership(_gameMainAddr);

        // 
        require(_maxPlayers >= 2, "Invalid max players");

        active = true;
        tableId = _tableId;
        tableName = _tableName;
        bankerAddr = _bankerAddr;
        betAmount = _betAmount;
        maxPlayers = _maxPlayers;
        state = GameState.WAITING;
        gameMainAddr = _gameMainAddr;
        creationTimestamp = block.timestamp;
        lastActivityTimestamp = block.timestamp; // 
        bankerFeePercent = _bankerFeePercent;
        implementationVersion = _implementationVersion;

        firstBetX = _firstRaise;
        secondBetX = _secondRaise;


        refreshConfig();

        emit GameTableInitialized(address(this), _bankerAddr, _implementationVersion);
    }

    //
    function refreshConfig() internal {
        IGameMain gameMain = IGameMain(gameMainAddr);
        GameConfig memory config = gameMain.getGameConfig();
        playerTimeout = config.playerTimeout * playerCount;
        liquidatorFeePercent = config.liquidatorFeePercent;
        tableInactiveTimeout = config.tableInactiveTimeout;
        roomCardAddr = config.roomCardAddress;
        rewardPoolAddr = config.rewardPoolAddress;
    }

    // getintstring
    function getPlayerAddresses() public view returns (address[] memory) {
        // 
        if (playerCount == 0) {
            return new address[](0);
        }

        address[] memory playerAddresses = new address[](playerCount);
        uint count = 0;
        for(uint i = 0; i < players.length; i++){
            if(players[i].addr != address(0)){
                playerAddresses[count] = players[i].addr;
                count++;
            }
        }

        return playerAddresses;
    }

    // table
    function editGameTable(
        string memory _tableName,
        uint256 _betAmount,
        uint8 _maxPlayers,
        uint8 _bankerFeePercent,
        uint8 _firstRaise,
        uint8 _secondRaise
    ) external onlyBanker nonReentrant {
        IGameMain gameMain = IGameMain(gameMainAddr);
        GameConfig memory config = gameMain.getGameConfig();

        require(_betAmount != 0, "Bet amount too small");
        require(_maxPlayers > 0 && _maxPlayers <= config.maxPlayers, "Invalid max players");
        require(_bankerFeePercent <= config.maxBankerFeePercent, "Invalid banker fee percent");
        require(bytes(_tableName).length > 0 && bytes(_tableName).length <= 20, "Invalid table name");
        require(_firstRaise >= 1 && _firstRaise <= 4, "Invalid first raise");
        require(_secondRaise >= 1 && _secondRaise <= 4, "Invalid second raise");

        tableName = _tableName;
        betAmount = _betAmount;
        maxPlayers = _maxPlayers;
        bankerFeePercent = _bankerFeePercent;
        firstBetX = _firstRaise;
        secondBetX = _secondRaise;

        emit GameTableChanged(address(this));
    }

    /**
     * @dev 
     */
    function _updateLastActivity() internal {
        lastActivityTimestamp = block.timestamp;
    }

    modifier onlyParticipant() {
        if(msg.sender != bankerAddr) {
            _getPlayer(msg.sender);
        }
        _;
    }

    /**
     * @dev 
     */
    modifier onlyBanker() {
        require(msg.sender == bankerAddr, "not banker");
        _;
    }

    /**
     * @dev 
     */
    modifier onlyPlayers() {
        _getPlayer(msg.sender);
        _;
    }

    /**
     * @dev 
     */
    modifier onlyGameMain() {
        require(msg.sender == gameMainAddr, "only main contract can call");
        _;
    }


    /**
     * @dev 
     * @param poolId ID
     */
    function setTableRewardPool(uint256 poolId) external onlyBanker nonReentrant {
        require(rewardPoolAddr != address(0), "Invalid reward pool address");
        bool isBankerPool = IRewardPool(rewardPoolAddr).isBankerPool(bankerAddr, poolId);
        require(isBankerPool, "poolId is not your pool");

        rewardPoolId = poolId;
    }

    /**
     * @dev 
     */
    function removeTableRewardPool() external onlyBanker nonReentrant {
        rewardPoolId = 0;
    }

    //
    function _addPlayer(address playerAddr) internal returns (uint256) {
        //players
        for(uint8 i = 0; i < players.length; i++){
            if(players[i].addr == address(0)){
                players[i].addr = playerAddr;
                players[i].state = PlayerState.JOINED;
                players[i].totalBet = 0;
                players[i].hasActedThisRound = false;
                players[i].cards = [0, 0, 0, 0, 0];
                players[i].cardType = CardType.NONE;
                playerCount++;
                return i;
            }
        }

        players.push(BBPlayer({
            addr: playerAddr,
            state: PlayerState.JOINED,
            totalBet: 0,
            hasActedThisRound: false,
            isWinner: false,
            cards: [0, 0, 0, 0, 0],
            cardType: CardType.NONE,
            __gap: [uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0)]
        })); 

        playerCount++;
        return players.length - 1;   
    }

    function _getPlayerIndex(address playerAddr) internal view returns (uint8) {
        for (uint8 i = 0; i < players.length; i++) {
            if (players[i].addr == playerAddr) {
                return i;
            }
        }
        require(false, "Player not found");
        return 0;
    }

    function _getPlayer(address playerAddr) internal view returns (uint8 index, BBPlayer storage) {
        uint8 playerIndex = _getPlayerIndex(playerAddr);
        return (playerIndex, players[playerIndex]);
    }

    //
    function _removePlayer(address playerAddr) internal {
        uint8 index = _getPlayerIndex(playerAddr);
        _removePlayerByIndex(index);
    }

    function _removePlayerByIndex(uint8 index) internal {
        players[index].addr = address(0);
        players[index].state = PlayerState.NONE;
        players[index].totalBet = 0;
        players[index].hasActedThisRound = false;
        players[index].isWinner = false;
        players[index].cards = [0, 0, 0, 0, 0];
        players[index].cardType = CardType.NONE;

        playerCount--;
    }

    function _isPlayerExists(address playerAddr) internal view returns (bool) {
        for (uint8 i = 0; i < players.length; i++) {
            if (players[i].isValid() && players[i].addr == playerAddr) {
                return true;
            }
        }
        return false;
    }

    // ID
    function updateChatGroupId(string memory _chatGroupId) external onlyBanker {
        chatGroupId = _chatGroupId;
    }


    /**
     * @dev 
     */
    function playerJoin() external nonReentrant {
        address playerAddr = msg.sender;
        require(!_isPlayerExists(playerAddr), "Player already joined");
        require(state == GameState.WAITING, "Game not in waiting state");
        require(playerCount < maxPlayers, "Max players reached");

        _addPlayer(playerAddr);

        _updateLastActivity();

        emit GameTableChanged(address(this));
    }

    /**
     * @dev 
     */
    function playerReady(bytes32 randomValue) external payable onlyPlayers nonReentrant {
        require(randomValue != bytes32(0), "Invalid random value");
        require(state == GameState.WAITING, "Game not in waiting state");
        address playerAddr = msg.sender;
        (, BBPlayer storage player) = _getPlayer(playerAddr);
        require(player.state == PlayerState.JOINED, "Player not in joined state");
        require(msg.value == betAmount, "Insufficient funds");

        player.totalBet = betAmount;
        totalPrizePool += betAmount;
        playerReadyCount++;

        player.playerReady();

        _updateLastActivity();

        _updateFinalSeed(randomValue);

        emit GameTableChanged(address(this));
    }

    /**
     * @dev 
     */
    function playerUnready() external payable onlyPlayers nonReentrant {
        require(state == GameState.WAITING, "Game not in waiting state");
        address playerAddr = msg.sender;
        (, BBPlayer storage player) = _getPlayer(playerAddr);
        require(player.state == PlayerState.READY, "Player not in ready state");

        totalPrizePool -= betAmount;
        playerReadyCount--;
        player.totalBet = 0;

        player.playerUnready();

        _updateLastActivity();

        //  -  call  transfer
        (bool success, ) = payable(playerAddr).call{value: betAmount}("");
        require(success, "transfer failed");

        emit GameTableChanged(address(this));
    }

    /**
     * @dev 
     */
    function playerQuit() external payable onlyPlayers nonReentrant {
        require(state == GameState.WAITING || state == GameState.SETTLED, "Game not in waiting or settled state");
        address playerAddr = msg.sender;
        (uint8 playerIndex, BBPlayer storage player) = _getPlayer(playerAddr);
        if(state == GameState.WAITING){
            require(player.state == PlayerState.JOINED || player.state == PlayerState.READY, "Player not in JOINED or READY state");

            // 
            uint256 amountToReturn = player.totalBet;

            if(player.state == PlayerState.READY){
                playerReadyCount--;
                totalPrizePool -= amountToReturn;
            }

            // 
            _removePlayerByIndex(playerIndex);

            if(amountToReturn > 0){
                //
                (bool success, ) = payable(playerAddr).call{value: amountToReturn}("");
                require(success, "transfer failed");
            }
        }else{
            // 
            _removePlayerByIndex(playerIndex);
        }  


        _updateLastActivity();

        emit GameTableChanged(address(this));
    }

    /**
     * @dev 
     */
    function bankerRemovePlayer(address playerAddr) external onlyBanker nonReentrant {
        require(state == GameState.WAITING, "Game not in waiting state");
        require(playerAddr != bankerAddr, "Can not remove banker");
        (uint8 playerIndex, BBPlayer storage player) = _getPlayer(playerAddr);


        uint256 amountToReturn = 0;
        if(player.totalBet > 0){
            amountToReturn = player.totalBet;
        }
        if(player.state == PlayerState.READY){
            playerReadyCount--;
            totalPrizePool -= amountToReturn;
        }
        _removePlayerByIndex(playerIndex);
        _updateLastActivity();

        // 
        if(amountToReturn > 0){
            (bool success, ) = payable(playerAddr).call{value: amountToReturn}("");
            require(success, "transfer failed");
        }


        emit GameTableChanged(address(this));
    }

    function _updateFinalSeed(bytes32 randomValue) internal {
        finalSeed = uint256(keccak256(abi.encodePacked(
            finalSeed,
            block.prevrandao,
            block.timestamp,
            msg.sender,
            randomValue
        )));
    }

    function startGame(uint256 roomCardId, bytes32 randomValue) external payable onlyBanker nonReentrant {
        require(randomValue!= bytes32(0), "Invalid random value");
        // 
        refreshConfig();

        require(roomCardId != 0, "Invalid room card params");
        require(roomCardAddr != address(0), "Invalid room card contract");
        require(playerReadyCount == playerCount, "Not all players ready");
        require(state == GameState.WAITING, "Game not in waiting state");
        require(playerCount >= 2, "Not enough players");

        // 
        require(msg.value == betAmount, "Insufficient funds");
        bankerStakeAmount = betAmount;

        //
        IRoomCardNFT roomCard = IRoomCardNFT(roomCardAddr);
        require(roomCard.validateParams(roomCardId, playerCount), "Invalid room card params");

        // 
        roomCard.consume(msg.sender, roomCardId);

        gameRound++;

        _updateFinalSeed(randomValue);

        _startFirstBetting();
    }

    function _resetGame() internal {
        playerContinuedCount = 0;
        gameStartTimestamp = 0;
        gameEndTimestamp = 0;
        currentRoundDeadline = 0;
        playerReadyCount = 0;
        totalPrizePool = 0;
        rewardAddr = address(0);
        rewardAmount = 0;
        dealerState.reset();
        for(uint i = 0; i < players.length; i++){
            if(players[i].isValid()){
                players[i].playerReset();
            }  
        }
        setState(GameState.WAITING);
    }

    function _canSettleGame() internal view returns (bool) {
        if(state == GameState.WAITING || state == GameState.SETTLED || state == GameState.LIQUIDATED){
            return false;
        }
        if(state == GameState.ENDED){
            return true;
        }


        if(state == GameState.FIRST_BETTING || state == GameState.SECOND_BETTING){
            (uint8 foldedCount, uint8 totalActed) = _getPlayerActionCounts();

            bool onlyOneLeft = playerContinuedCount == 1 && foldedCount == playerCount - 1;
            bool noOneLeft = foldedCount == playerCount;
            bool isDeadlinePassed = _isTimeOut();
            bool noOneActed = totalActed == 0;
            bool lessOneContinued = playerContinuedCount <= 1;  //

            if (onlyOneLeft || noOneLeft || (isDeadlinePassed && lessOneContinued) || (isDeadlinePassed && noOneActed)) {
                // 
                return true;
            } else {
                return false;
            }
        }

        return false;
    }

    /**
     * @dev 
     */
    function nextStep(bytes32 randomValue) external onlyBanker nonReentrant {
        require(randomValue != bytes32(0), "Invalid random value");
        if(_canSettleGame()){
            _settleGame();

            _updateFinalSeed(randomValue);
            _updateLastActivity();
            emit GameTableChanged(address(this));
            return;
        }

        (bool canMove, , string memory reason) = canMoveToNextStep();
        require(canMove, reason);
        if(state == GameState.FIRST_BETTING) {
            _startSecondBetting();
        }
        else if(state == GameState.SECOND_BETTING) {
            _endGame();
        }
        else if(state == GameState.SETTLED) {
            _resetGame();
        }

        // 
        for(uint i = 0; i < players.length; i++){
            if(players[i].isValid()){
                players[i].hasActedThisRound = false;
            }
        }

        _updateFinalSeed(randomValue);
        _updateLastActivity();
        emit GameTableChanged(address(this));
    }

    function _dealCardsByRound(uint8 round) internal {
        dealerState.initialize(finalSeed);
        if(round == 1){
            // 3
            for(uint i = 0; i < players.length; i++){
                if(players[i].isValid()){
                    uint8[] memory newCards = dealerState.dealCardsByRoundForPlayer(players[i].addr, round);
                    for(uint j = 0; j < 3; j++){
                        players[i].cards[j] = newCards[j];
                    }
                }
            }
        }else{
            // 
            for(uint i = 0; i < players.length; i++){
                if(players[i].isValid() && players[i].state == PlayerState.ACTIVE){
                    uint8[] memory newCards = dealerState.dealCardsByRoundForPlayer(players[i].addr, round);
                    // 
                    for(uint j = 0; j < newCards.length; j++){
                        players[i].cards[j + round + 1] = newCards[j];
                    }
                }
            }
        }
    }

    /**
     * @dev 
     */
    function _startFirstBetting() internal {
        // active
        for (uint256 i = 0; i < players.length; i++) {
            BBPlayer storage player = players[i];
            if(player.isValid()){
                player.state = PlayerState.ACTIVE;
            }
        }

        // 
        dealerState.reset();

        // 
        _dealCardsByRound(1);

        // 
        setState(GameState.FIRST_BETTING);
    }

    function _getPlayerActionCounts() internal view returns (uint8, uint8){
        uint8 foldedCount = 0;
        uint8 actedCount = 0;
        for(uint i = 0; i < players.length; i++){
            BBPlayer storage player = players[i];
            if(player.isValid()){
                if(player.state == PlayerState.FOLDED){
                    foldedCount++;
                }
                if(player.hasActedThisRound){
                    actedCount++;
                }
            }
        }

        return (foldedCount, actedCount);
    }



    function _startSecondBetting() internal {
        for(uint i = 0; i < players.length; i++){
            BBPlayer storage player = players[i];
            if(player.isValid() && !player.hasActedThisRound){
                player.playerFold();
            }
        }

        _dealCardsByRound(2);
        setState(GameState.SECOND_BETTING);
        playerContinuedCount = 0;
    }

    function _endGame() internal {
        for(uint i = 0; i < players.length; i++){
            BBPlayer storage player = players[i];
            if(player.isValid() && !player.hasActedThisRound){
                player.playerFold();
            }
        }

        _dealCardsByRound(3);
        setState(GameState.ENDED);
        _settleGame();
    }

    function _allPlayersActed() public view returns (bool){
        for(uint i = 0; i < players.length; i++){
            BBPlayer storage player = players[i];
            if(player.isValid()){
                if(player.state != PlayerState.FOLDED && !player.hasActedThisRound){
                    return false;
                }
            }
        }
        return true;
    }

    function canMoveToNextStep() public view returns (bool canMove, string memory title, string memory reason) {
        if (state == GameState.LIQUIDATED) {
            return (false, "", "Game has been liquidated");
        }else if(state == GameState.SETTLED){
            return (true, "Play Again", "");
        }else if (state == GameState.ENDED) {
            return (true, "Settle Game", "");
        }else if(state == GameState.SETTLED){
            return (true, "Play Again", "");
        }else if (state == GameState.FIRST_BETTING || state == GameState.SECOND_BETTING) {

            if (playerCount == 0) {
                // 
                return (false, "", "No players in game");
            }

            bool isDeadlinePassed = _isTimeOut();
            bool allPlayersActed = _allPlayersActed();

            if (_canSettleGame()) {
                return (true, "Settle Game", "");
            } else if (allPlayersActed || isDeadlinePassed) {
                return (true, "Next Round", "");
            } else {
                return (false, "Next Round", "Waiting for players to act");
            }
        }

        return (false, "", "Unknown state");
    }


    function _isTimeOut() internal view returns (bool) {
        return currentRoundDeadline > 0 && block.timestamp > currentRoundDeadline;
    }

    /**
     * @dev 
     */
    function playerFold(bytes32 randomValue) external onlyPlayers nonReentrant{
        require(randomValue!= bytes32(0), "Invalid random value");
        require(state == GameState.FIRST_BETTING || state == GameState.SECOND_BETTING, "Game not in playing state");
        address playerAddr = msg.sender;

        // 
        require(!_isTimeOut(), "action timeout");

        (, BBPlayer storage player) = _getPlayer(playerAddr);
        require(player.state == PlayerState.ACTIVE && player.hasActedThisRound == false, "Player not in active state or has acted");

        _updateLastActivity();
        player.playerFold();

        _updateFinalSeed(randomValue);

        emit GameTableChanged(address(this));
    }

    /**
     * @dev 
     */
    function playerContinue(bytes32 randomValue) external payable onlyPlayers nonReentrant{
        require(randomValue!= bytes32(0), "Invalid random value");
        require(state == GameState.FIRST_BETTING || state == GameState.SECOND_BETTING, "Game not in playing state");
        address playerAddr = msg.sender;

        uint256 needBetAmount = 0;

        if(state == GameState.FIRST_BETTING){
            needBetAmount = betAmount * firstBetX;
        }else{
            needBetAmount = betAmount * secondBetX;
        }

        require(msg.value == needBetAmount, "Insufficient funds");

        // 
        require(!_isTimeOut(), "action timeout");

        (, BBPlayer storage player) = _getPlayer(playerAddr);
        require(player.state == PlayerState.ACTIVE && player.hasActedThisRound == false, "Player not in active state or has acted");

        player.playerContinue(needBetAmount);
        playerContinuedCount++;
        totalPrizePool += needBetAmount;

        _updateLastActivity();

        _updateFinalSeed(randomValue);

        emit GameTableChanged(address(this));
    }

    function _gameTimeout() internal view returns (bool)  {
        require(state == GameState.FIRST_BETTING || state == GameState.SECOND_BETTING, "Game not in playing state"); 

        // 
        return block.timestamp > lastActivityTimestamp + tableInactiveTimeout;
    }

    /**
     * @dev 
     */
    function playerSettle() external payable onlyPlayers nonReentrant{
        require(_gameTimeout(), "Game not timeout");

        _settleGame();
    }

    function _settleGame() internal {
        setState(GameState.SETTLED);

        gameEndTimestamp = block.timestamp;


        bool noOneContinued = playerContinuedCount == 0;

        // 
        uint256 bankerFee = (totalPrizePool * bankerFeePercent) / 100;
        if(noOneContinued){
            // 
            bankerFee = 0;
        }
        uint256 remainingPrizePool = totalPrizePool - bankerFee;

        uint256 bankerTotal = bankerFee + bankerStakeAmount;
        bankerStakeAmount = 0;

        // 
        if (playerContinuedCount == 1) {
            _settleOneContinuedPlayer(remainingPrizePool);
        }
        // 
        else if (noOneContinued) {
            _settleAllFolded();
        }
        // 
        else {
            _settleNormalGame(remainingPrizePool);
        }

        // +
        if (bankerTotal > 0) {
            totalIncome += bankerFee;
            (bool success, ) = payable(bankerAddr).call{value: bankerTotal}("");
            require(success, "transfer to banker failed");
        }

        // 
        if (rewardPoolAddr != address(0) && rewardPoolId != 0) {
            address[] memory playerAddresses = getPlayerAddresses();
            IRewardPool rewardPool = IRewardPool(rewardPoolAddr);
            try rewardPool.tryDistributeReward(rewardPoolId, playerAddresses, finalSeed) returns (address winAddr, uint256 winAmount){
                if(winAddr != address(0)){
                    // 
                    rewardAddr = winAddr;
                    rewardAmount = winAmount;
                }
            } catch {
            }
        }

        emit GameTableChanged(address(this));
    }

    /**
     * @dev 
     */
    function _settleOneContinuedPlayer(uint256 remainingPrizePool) internal {
        // 
        address[] memory winnerAddrs = new address[](1);
        uint256[] memory playerBets = new uint256[](playerCount);
        uint8[5][] memory playerCards = new uint8[5][](playerCount);
        address[] memory playerAddresses = new address[](playerCount);
        uint256 winnerCount = 0;

        // 
        address continuedPlayer = address(0);
        uint8 index = 0;
        for (uint i = 0; i < players.length; i++) {
            BBPlayer storage player = players[i];
            if (player.isValid()) {
                // 
                if (player.state == PlayerState.ACTIVE &&
                    player.hasActedThisRound) {
                    continuedPlayer = player.addr;
                    winnerAddrs[winnerCount] = player.addr;
                    player.isWinner = true;
                    winnerCount++;
                }

                playerBets[index] = player.totalBet;
                playerCards[index] = player.cards;
                playerAddresses[index] = player.addr;

                index++;
            }
        }

        // 
        if (continuedPlayer != address(0)) {
            (bool success, ) = payable(continuedPlayer).call{value: remainingPrizePool}("");
            require(success, "transfer to winners failed");
        }

        emit CreateGameHistory(address(this), gameRound, gameStartTimestamp, gameEndTimestamp, playerAddresses, winnerAddrs, playerBets, playerCards);
    }

    /**
     * @dev 
     */
    function _settleAllFolded() internal {
        // 
        address[] memory refundAddresses = new address[](playerCount);
        uint256[] memory refundAmounts = new uint256[](playerCount);
        uint256[] memory playerBets = new uint256[](playerCount);
        uint8[5][] memory playerCards = new uint8[5][](playerCount);
        address[] memory playerAddresses = new address[](playerCount);
        address[] memory winnerAddrs = new address[](0);
        uint256 refundCount = 0;

        // 
        uint8 index = 0;
        for (uint i = 0; i < players.length; i++) {
            if (players[i].isValid()) {
                BBPlayer storage player = players[i];

                // 
                uint256 refundAmount = player.totalBet;
                if (refundAmount > 0) {
                    refundAddresses[refundCount] = player.addr;
                    refundAmounts[refundCount] = refundAmount;
                    refundCount++;
                }

                playerBets[index] = players[i].totalBet;
                playerCards[index] = players[i].cards;
                playerAddresses[index] = players[i].addr;
                index++;
            }        
        }

        // 
        for (uint i = 0; i < refundCount; i++) {
            (bool success, ) = payable(refundAddresses[i]).call{value: refundAmounts[i]}("");
            require(success, "refund failed");
        }

        emit CreateGameHistory(address(this), gameRound, gameStartTimestamp, gameEndTimestamp, playerAddresses, winnerAddrs, playerBets, playerCards);
    }


    /**
     * @dev 
     */
    function _settleNormalGame(uint256 remainingPrizePool) internal {
        CardType _maxCardType = CardType.NONE;

        // 
        address[] memory winnerAddrs = new address[](playerCount);
        uint256[] memory playerBets = new uint256[](playerCount);
        uint8[5][] memory playerCards = new uint8[5][](playerCount);
        address[] memory playerAddresses = new address[](playerCount);
        uint256 winnerCount = 0;


        // 
        uint8 index = 0;
        for (uint i = 0; i < players.length; i++) {
            BBPlayer storage player = players[i];
            if (player.isValid()) {
                player.cardType = BBCardUtils.calculateCardType(player.cards);
                // 
                if (uint8(player.cardType) > uint8(_maxCardType)) {
                    _maxCardType = player.cardType;
                }

                playerBets[index] = player.totalBet;
                playerCards[index] = player.cards;
                playerAddresses[index] = player.addr;
                index++;
            }
        }

        // 
        if (_maxCardType == CardType.NONE) {
            (winnerAddrs, winnerCount) = _settleNormalGameWithNoBull();
        } else {
            // 
            for (uint i = 0; i < players.length; i++) {
                BBPlayer storage player = players[i];
                if (player.isValid()) {
                    // 
                    if (player.cardType == _maxCardType) {
                        winnerAddrs[winnerCount] = player.addr;
                        player.isWinner = true;
                        winnerCount++;
                    }
                }
            }
        }

        // 
        assembly ("memory-safe")  {
            mstore(winnerAddrs, winnerCount)
        }


        // 
        if (winnerCount > 0) {
            // 
            uint256 prizePerWinner = remainingPrizePool / winnerCount;

            // 
            for (uint i = 0; i < winnerCount; i++) {
                address winnerAddr = winnerAddrs[i];
                (bool success, ) = payable(winnerAddr).call{value: prizePerWinner}("");
                require(success, "transfer to winners failed");
            }

            // 
            uint256 remainingPrize = remainingPrizePool - (prizePerWinner * winnerCount);
            if (remainingPrize > 0) {
                (bool success, ) = payable(winnerAddrs[0]).call{value: remainingPrize}("");
                require(success, "transfer to winners failed");
            }
        }

        emit CreateGameHistory(address(this), gameRound, gameStartTimestamp, gameEndTimestamp, playerAddresses, winnerAddrs, playerBets, playerCards);
    }

    /**
     * @dev 
     */
    function _settleNormalGameWithNoBull() internal view returns (address[] memory, uint256){
        uint8 maxCard = 0;
        uint256 winnerCount = 0;
        address[] memory winnerAddrs = new address[](playerCount);

        // 
        for (uint i = 0; i < players.length; i++) {
            BBPlayer storage player = players[i];
            if(player.isValid()){
                // 
                uint8 playerMaxCard = 0;
                for (uint j = 0; j < 5; j++) {
                    uint8 cardValue = player.cards[j] % 13;
                    // A(1)K(13)
                    if (cardValue == 0) cardValue = 1; // A1
                    if (cardValue > playerMaxCard) {
                        playerMaxCard = cardValue;
                    }
                }

                // 
                if (playerMaxCard > maxCard) {
                    maxCard = playerMaxCard;
                }
            }
        }

        // 
        for (uint i = 0; i < players.length; i++) {
            BBPlayer storage player = players[i];
            if(player.isValid()){
                // 
                uint8 playerMaxCard = 0;
                for (uint j = 0; j < 5; j++) {
                    uint8 cardValue = player.cards[j] % 13;
                    // A(1)K(13)
                    if (cardValue == 0) cardValue = 1; // A1
                    if (cardValue > playerMaxCard) {
                        playerMaxCard = cardValue;
                    }
                }

                if (playerMaxCard == maxCard) {
                    winnerAddrs[winnerCount] = player.addr;
                    winnerCount++;
                }
            }  
        }

        // 
        assembly ("memory-safe")  {
            mstore(winnerAddrs, winnerCount)
        }

        return (winnerAddrs, winnerCount);
    }

    /**
     * @dev 
     * 
     * 
     */
    function liquidateInactiveTable() external onlyPlayers nonReentrant {
        // 
        require(block.timestamp > lastActivityTimestamp + tableInactiveTimeout, "Table not inactive");
        require(msg.sender != bankerAddr, "Banker cannot liquidate");
        require(state == GameState.FIRST_BETTING || state == GameState.SECOND_BETTING, "Table not in gaming");

        //  ()
        uint256 liquidatorReward = bankerStakeAmount * liquidatorFeePercent / 100;

        // 
        uint256 remainingBankerBet = bankerStakeAmount - liquidatorReward;
        uint256 playerRewardTotal = 0;

        // 
        uint8 eligiblePlayerCount = 0;
        for(uint8 i = 0; i < players.length; i++){
            BBPlayer storage player = players[i];
            if(player.isValid() && player.addr != bankerAddr){
                eligiblePlayerCount++;
            }
        }

        // 
        address[] memory paymentAddresses = new address[](eligiblePlayerCount);
        uint256[] memory paymentAmounts = new uint256[](eligiblePlayerCount);
        uint256 paymentCount = 0;

        // 
        if (eligiblePlayerCount == 0) {
            liquidatorReward = bankerStakeAmount;
            remainingBankerBet = 0;
        } else {
            // 
            uint256 rewardPerPlayer = remainingBankerBet / eligiblePlayerCount;

            // 
            for (uint i = 0; i < players.length; i++) {
                BBPlayer storage player = players[i];
                if(player.isValid() && player.addr != bankerAddr){
                    uint256 totalPayment = 0;

                    //  + 
                    totalPayment += player.totalBet;

                    // 
                    totalPayment += rewardPerPlayer;

                    // 
                    if (totalPayment > 0) {
                        paymentAddresses[paymentCount] = player.addr;
                        paymentAmounts[paymentCount] = totalPayment;
                        paymentCount++;
                    }

                    playerRewardTotal += rewardPerPlayer;
                }


            }
        }

        // 
        uint256 actualDistributed = playerRewardTotal + liquidatorReward;
        if (actualDistributed < bankerStakeAmount) {
            liquidatorReward += (bankerStakeAmount - actualDistributed);
        }

        // 
        for (uint i = 0; i < players.length; i++) {
            if(players[i].isValid()){
                players[i].playerReset();
            }
        }

        playerCount = 0;
        playerReadyCount = 0;
        playerContinuedCount = 0;
        totalPrizePool = 0;
        gameLiquidatedCount++;

        setState(GameState.LIQUIDATED);

        // 
        // 
        for (uint i = 0; i < paymentCount; i++) {
            (bool otherSuccess, ) = payable(paymentAddresses[i]).call{value: paymentAmounts[i]}("");
            require(otherSuccess, "transfer to players failed");
        }

        // 
        (bool liquidatorSuccess, ) = payable(msg.sender).call{value: liquidatorReward}("");
        require(liquidatorSuccess, "transfer to liquidator failed");

        emit GameTableChanged(address(this));
    }


    /**
     * @dev 
     */
    function setState(GameState _state) internal {
        state = _state;

        // 
        if (_state == GameState.FIRST_BETTING || _state == GameState.SECOND_BETTING) {
            currentRoundDeadline = block.timestamp + playerTimeout;
        }

        _updateLastActivity(); // 

        emit GameTableChanged(address(this));
    }

    // 
    function getAllPlayerData() external view returns (BBPlayer[] memory) {
        BBPlayer[] memory playerData = new BBPlayer[](playerCount);
        uint8 index = 0;
        for (uint i = 0; i < players.length; i++) {
            if(players[i].isValid()){
                playerData[index] = players[i];
                playerData[index].cardType = BBCardUtils.calculateCardType(players[i].cards);
                index++;
            }
        }

        return playerData;
    }


    // 
    function getPlayerData(address playerAddr) external view returns (BBPlayer memory) {
        (, BBPlayer storage player) = _getPlayer(playerAddr);
        BBPlayer memory playerData = player;
        playerData.cardType = BBCardUtils.calculateCardType(player.cards);
        return playerData;
    }


    function getTableInfo() external view returns (GameTableView memory) {
        IRewardPool rewardPool = IRewardPool(rewardPoolAddr);
        (bool canNext, string memory nextTitle, string memory nextReason) = canMoveToNextStep();
        //  RewardPoolInfo 
        RewardPoolInfo memory emptyRewardPoolInfo;
        if (rewardPoolId != 0) {
            emptyRewardPoolInfo = rewardPool.getRewardPoolInfo(bankerAddr, rewardPoolId);
        }

        return GameTableView({
            // balance: address(this).balance / 1 ether,
            active: active,
            gameRound: gameRound,
            gameLiquidatedCount: gameLiquidatedCount,
            tableAddr: address(this),
            tableId: tableId,
            tableName: tableName,
            bankerAddr: bankerAddr,
            betAmount: betAmount,
            bankerFeePercent: bankerFeePercent,
            totalPrizePool: totalPrizePool,
            playerCount: playerCount,
            maxPlayers: maxPlayers,
            creationTimestamp: creationTimestamp,
            state: state,
            liquidatorFeePercent: liquidatorFeePercent,
            playerContinuedCount: playerContinuedCount,
            playerReadyCount: playerReadyCount,
            playerAddresses: getPlayerAddresses(),
            currentRoundDeadline: currentRoundDeadline,
            playerTimeout: playerTimeout,
            tableInactiveTimeout: tableInactiveTimeout,
            lastActivityTimestamp: lastActivityTimestamp,
            rewardPoolId: rewardPoolId,
            rewardPoolInfo: emptyRewardPoolInfo, // 
            implementationVersion: implementationVersion,
            firstBetX: firstBetX,
            secondBetX: secondBetX,
            bankerStakeAmount: bankerStakeAmount,
            canNext: canNext,
            nextTitle: nextTitle,
            nextReason: nextReason,
            rewardAddr: rewardAddr,
            rewardAmount: rewardAmount,
            chatGroupId: chatGroupId
        });
    }

    function getTableInfoShort() external view returns (GameTableInfoShort memory) {
        IRewardPool rewardPool = IRewardPool(rewardPoolAddr);

        //  RewardPoolInfo 
        RewardPoolInfo memory emptyRewardPoolInfo;
        if (rewardPoolId != 0) {
            emptyRewardPoolInfo = rewardPool.getRewardPoolInfo(bankerAddr, rewardPoolId);
        }

        return GameTableInfoShort({
            active: active,
            gameRound: gameRound,
            gameLiquidatedCount: gameLiquidatedCount,
            tableAddr: address(this),
            tableId: tableId,
            tableName: tableName,
            bankerAddr: bankerAddr,
            betAmount: betAmount,
            bankerFeePercent: bankerFeePercent,
            playerCount: playerCount,
            maxPlayers: maxPlayers,
            state: state,
            lastActivityTimestamp: lastActivityTimestamp,
            rewardPoolId: rewardPoolId,
            rewardPoolInfo: emptyRewardPoolInfo // 
        });
    }

    function isPlayer(address playerAddr) external view returns (bool) {
        _getPlayer(playerAddr);
        return true;
    }

    receive() external payable {}
}

contracts/BBInterfaces.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "./BBTypes.sol";
import "./BBStructs.sol";

interface IGameTableFactory {
    function createGameTable(
        uint256 tableId,
        string memory tableName,
        address bankerAddr,
        uint256 betAmount,
        uint8 maxPlayers,
        address gameMainAddr,
        uint8 bankerFeePercent,
        uint8 firstRaise,
        uint8 secondRaise
    ) external returns (address);
}

interface IGameTableImplementation {
    function bankerAddr() external view returns (address);
    function getTableInfo() external view returns (GameTableView memory);
    function getTableInfoShort() external view returns (GameTableInfoShort memory);
    function lastActivityTimestamp() external view returns (uint256);
    function state() external view returns (GameState);
    function rewardPoolId() external view returns (uint256);
    function initialize(
        uint256 _tableId,
        string memory _tableName,
        address _bankerAddr,
        uint256 _betAmount,
        uint8 _maxPlayers,
        address _gameMainAddr,
        uint8 _bankerFeePercent,
        uint8 _firstRaise,
        uint8 _secondRaise,
        uint256 _implementationVersion
    ) external;
}

interface IGameMain {
    function isValidGameTable(address) external view returns (bool);
    function liquidatorFeePercent() external view returns (uint256);
    function playerTimeout() external view returns (uint256);
    function tableInactiveTimeout() external view returns (uint256);
    function rewardPoolAddress() external view returns (address);
    function roomCardAddress() external view returns (address);
    function roomLevelAddress() external view returns (address);
    function getGameConfig() external view returns (GameConfig memory);
    function rewardPoolIsInUse(address, uint256) external view returns (bool);
}

interface IRewardPool{
    function tryDistributeReward(uint256 _poolId, address[] calldata _players, uint256 finalSeed) external  returns (address, uint256);
    function isBankerPool(address, uint256) external view returns (bool);
    function getRewardPoolInfo(address, uint256) external view returns (RewardPoolInfo memory);
}

interface IRoomCardNFT{
    function validateParams(uint256, uint8) external view returns (bool);
    function hasNft(address) external view returns (bool);
    function consume(address, uint256) external;
}

contracts/BBPlayer.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.28;

import "./BBTypes.sol";

struct BBPlayer {
    address addr;
    PlayerState state;

    uint256 totalBet;

    bool hasActedThisRound;   // 
    bool isWinner;   // 

    uint8[5] cards;
    CardType cardType;

    uint256[10] __gap;
}

library BBPlayerLib {
    /**
     * @dev 
     */
    function playerReady(BBPlayer storage self) internal {
        self.state = PlayerState.READY;
    }

    /**
     * @dev 
     */
    function playerUnready(BBPlayer storage self) internal {
        self.state = PlayerState.JOINED;
    }

    /**
     * @dev 
     */
    function playerJoin(BBPlayer storage self) internal {
        self.state = PlayerState.JOINED;
    }

    /**
     * @dev 
     */
    function playerFold(BBPlayer storage self) internal {
        self.state = PlayerState.FOLDED;
        self.hasActedThisRound = true;
    }

    /**
     * @dev 
     */
    function playerContinue(BBPlayer storage self, uint256 additionalBet) internal {
        self.totalBet += additionalBet;
        self.state = PlayerState.ACTIVE;
        self.hasActedThisRound = true;
    }

    /**
     * @dev 
     */
    function isValid(BBPlayer storage self) internal view returns (bool) {
        return self.addr != address(0);
    }

    /**
     * @dev 
     */
    function playerReset(BBPlayer storage self) internal {
        self.state = PlayerState.JOINED;
        self.hasActedThisRound = false;
        self.totalBet = 0;
        self.isWinner = false;
        self.cards = [0, 0, 0, 0, 0];
        self.cardType = CardType.NONE;
    }
}

contracts/BBRewardPool.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.28;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "./BBTypes.sol";
import "./BBStructs.sol";
import "./BBInterfaces.sol";

/**
 * @title BBRewardPool
 * @dev 
 */
contract BBRewardPool is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable
{
    

    // 
    uint256 private nextPoolId;                           // ID
    mapping(address => RewardPoolInfo[]) private bankerPools;      // 

    // 
    address public gameMainAddr;

    // 100 = 100%
    uint8 private constant MAX_PROBABILITY = 100;

    //  50  slot 
    uint256[50] private __gap;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev 
     * @param _gameMainAddr 
     */
    function initialize(address _gameMainAddr) public initializer {
        __Ownable_init(msg.sender);
        __ReentrancyGuard_init();
        __UUPSUpgradeable_init();

        require(_gameMainAddr != address(0), "Invalid game main address");
        gameMainAddr = _gameMainAddr;
        nextPoolId = 1;
    }

    /**
     * @dev 
     * @param name 
     * @param _totalReward 
     * @param _rewardPerGame 
     * @param _winProbability 1010%
     */
    function createRewardPool(string calldata name, uint256 _totalReward, uint256 _rewardPerGame, uint256 _winProbability) external payable nonReentrant {
        // 
        require(_rewardPerGame > 0 && _totalReward > 0, "Invalid reward amount");
        require(_winProbability > 0 && _winProbability <= MAX_PROBABILITY, "Invalid win probability");
        require(msg.value == _totalReward, "Insufficient funds");

        // 
        RewardPoolInfo memory pool;
        pool.name = name;
        pool.poolId = nextPoolId++;
        pool.banker = msg.sender;
        pool.totalAmount = _totalReward;
        pool.rewardPerGame = _rewardPerGame;
        pool.winProbability = _winProbability;
        pool.remainingAmount = _totalReward;
        pool.__gap = [uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0)];

        // 
        bankerPools[msg.sender].push(pool);

        emit RewardPoolCreated(pool.poolId, msg.sender, msg.value, _rewardPerGame, _winProbability);
    }

    //
    function isBankerPool(address _banker, uint256 _poolId) external view returns (bool) {
        RewardPoolInfo[] memory pools = bankerPools[_banker];
        for (uint256 i = 0; i < pools.length; i++) {
            if (pools[i].poolId == _poolId) {
                return true;
            }
        }
        return false;
    }

    /**
     * @dev 
     * @param _poolId ID
     */
    function removeRewardPool(uint256 _poolId) external nonReentrant {
        RewardPoolInfo[] storage pools = bankerPools[msg.sender];
        require(pools.length > 0, "Reward pool not active");
        
        // 
        uint256 poolIndex = type(uint256).max;
        for (uint256 i = 0; i < pools.length; i++) {
            if (pools[i].poolId == _poolId) {
                poolIndex = i;
                break;
            }
        }
        
        require(poolIndex != type(uint256).max, "Reward pool not active");

        if(IGameMain(gameMainAddr).rewardPoolIsInUse(msg.sender, _poolId)){
            require(false, "Reward pool in use");
        }
        
        RewardPoolInfo storage pool = pools[poolIndex];
        uint256 remainingAmount = pool.remainingAmount;

        // 
        if (poolIndex != pools.length - 1) {
            pools[poolIndex] = pools[pools.length - 1];
        }
        pools.pop();

        if(remainingAmount > 0){
            // 
            (bool success, ) = payable(msg.sender).call{value: remainingAmount}("");
            require(success, "Transfer failed");
        }
        

        emit RewardPoolRemoved(_poolId, msg.sender, remainingAmount);
    }

    /**
     * @dev 
     * @param _poolId 
     * @param _players 
     * @param finalSeed 
     * @return 
     */
    function tryDistributeReward(uint256 _poolId, address[] calldata _players, uint256 finalSeed) external nonReentrant returns (address, uint256) {
        address tableAddr = msg.sender;
        address bankerAddr = IGameTableImplementation(tableAddr).bankerAddr();
        require(bankerAddr != address(0), "invalid banker address");

        RewardPoolInfo storage pool = bankerPools[bankerAddr][_poolId];
        if (pool.poolId == 0) return (address(0), 0);

        // 
        if (pool.remainingAmount < pool.rewardPerGame) return (address(0), 0);

        // 
        if (_players.length == 0) return (address(0), 0);

        // 
        uint256 randomValue = _generateRandomNumber(finalSeed, _poolId, tableAddr) % MAX_PROBABILITY;

        // 
        if (randomValue < pool.winProbability) {
            // 
            uint256 winnerIndex = _generateRandomNumber(finalSeed, _poolId + 1, tableAddr) % _players.length;
            address winner = _players[winnerIndex];

            // 
            pool.remainingAmount -= pool.rewardPerGame;

            // 
            (bool success, ) = payable(winner).call{value: pool.rewardPerGame}("");
            require(success, "Transfer failed");

            emit RewardDistributed(_poolId, tableAddr, winner, pool.rewardPerGame);
            return (winner, pool.rewardPerGame);
        }

        return (address(0), 0);

    }

    /**
     * @dev 
     * @param _banker 
     * @return 
     */
    function getBankerPools(address _banker) external view returns (RewardPoolInfo[] memory) {
        return bankerPools[_banker];
    }

    /**
     * @dev 
     * @param _banker 
     * @param _poolId ID
     * @return 
     */
    function getRewardPoolInfo(address _banker, uint256 _poolId) external view returns (RewardPoolInfo memory) {
        RewardPoolInfo[] memory pools = bankerPools[_banker];
        for (uint256 i = 0; i < pools.length; i++) {
            if (pools[i].poolId == _poolId) {
                return pools[i];
            }
        }
        return RewardPoolInfo(0, "", address(0), 0, 0, 0, 0, [uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0)]);
    }

    /**
     * @dev 
     * @param _banker 
     * @param _poolId ID
     * @param _amount 
     */
    function _updateRewardPoolAmount(address _banker, uint256 _poolId, uint256 _amount) internal {
        RewardPoolInfo[] storage pools = bankerPools[_banker];
        for (uint256 i = 0; i < pools.length; i++) {
            if (pools[i].poolId == _poolId) {
                require(pools[i].remainingAmount >= _amount, "Insufficient funds");
                pools[i].remainingAmount -= _amount;
                return;
            }
        }
        require(false, "Reward pool not active");
    }

    /**
     * @dev 
     * @param _seed1 
     * @param _seed2 
     * @param _seed3 
     * @return 
     */
    function _generateRandomNumber(uint256 _seed1, uint256 _seed2, address _seed3) internal view returns (uint256) {
        return uint256(keccak256(abi.encodePacked(
            _seed1,
            _seed2,
            _seed3,
            block.timestamp,
            block.difficulty,
            blockhash(block.number - 1)
        )));
    }

    /**
     * @dev 
     * @param _gameMainAddr 
     */
    function setGameMainAddress(address _gameMainAddr) external onlyOwner {
        require(_gameMainAddr != address(0), "Invalid game main address");
        gameMainAddr = _gameMainAddr;
        emit GameMainAddressUpdated(_gameMainAddr);
    }

    /**
     * @dev UUPS
     * @param newImplementation 
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    // 
    event RewardPoolCreated(uint256 indexed poolId, address indexed banker, uint256 totalAmount, uint256 rewardPerGame, uint256 winProbability);
    event RewardPoolRemoved(uint256 indexed poolId, address indexed banker, uint256 remainingAmount);
    event TableRewardPoolSet(address indexed tableAddr, uint256 indexed poolId, address indexed banker);
    event TableRewardPoolRemoved(address indexed tableAddr, uint256 indexed poolId, address indexed banker);
    event RewardDistributed(uint256 indexed poolId, address indexed tableAddr, address indexed winner, uint256 amount);
    event GameMainAddressUpdated(address indexed gameMainAddr);

    
}

contracts/BBRoomCardNFT.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.28;

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import "./BBStructs.sol";
import "./BBInterfaces.sol";

/**
 * @title BBRoomCard
 * @dev Niu Niu game room card NFT contract with dynamic card types
 */
contract BBRoomCardNFT is
    Initializable,
    ERC721Upgradeable,
    ERC721EnumerableUpgradeable,
    OwnableUpgradeable,
    UUPSUpgradeable
{
    using Strings for uint256;

    // Room card type structure - combines type and config in one structure
    

    // Used to generate unique token IDs
    uint256 private _tokenIdCounter;

    // Used to generate unique card type IDs
    uint256 private _nftTypeIdCounter;

    // Room card base URI
    string private _baseTokenURI;

    // Game main contract address
    address public gameMainAddress;

    // Card types by ID
    mapping(uint256 => RoomCardNftType) public nftTypes;

    // Card type ID corresponding to each token ID
    mapping(uint256 => uint256) public tokenNftTypes;

    // Array of all card type IDs
    uint256[] private _allNftTypeIds;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialization function, replaces constructor
     */
    function initialize(
        string memory name,
        string memory symbol,
        string memory baseTokenURI
    ) public initializer {
        __ERC721_init(name, symbol);
        __ERC721Enumerable_init();
        __Ownable_init(msg.sender);
        __UUPSUpgradeable_init();

        _baseTokenURI = baseTokenURI;
        _tokenIdCounter = 1;
        _nftTypeIdCounter = 1; // Start from 1
    }

    /**
     * @dev Set the game main contract address
     * @param _gameMainAddress Game main contract address
     */
    function setGameMainAddress(address _gameMainAddress) external onlyOwner {
        require(_gameMainAddress != address(0), "Invalid game main address");
        gameMainAddress = _gameMainAddress;
    }

    modifier onlyGameTable() {
        bool isValidTable = false;
        if (gameMainAddress != address(0)) {
            if(IGameMain(gameMainAddress).isValidGameTable(msg.sender)){
                isValidTable = true;
            }else{
                isValidTable = false;
            }
        }
        require(isValidTable, "Only game table can call");
        _;
    }

    /**
     * @dev Add a new card type
     * @param name Name of the card type
     * @param maxPlayers Maximum number of players allowed
     * @param price Price to purchase this card
     * @param uriSuffix URI suffix for metadata
     * @return The ID of the newly created card type
     */
    function addType(
        string memory name,
        uint8 maxPlayers,
        uint256 price,
        string memory uriSuffix,
        uint256 maxMint,
        string memory rarity
    ) external onlyOwner returns (uint256) {
        require(bytes(name).length > 0, "Name cannot be empty");
        require(maxPlayers > 1, "Max players must be greater than 1");
        require(price > 0, "Price must be greater than 0");
        require(maxMint > 0, "Max mint must be greater than 0");
        require(bytes(rarity).length > 0, "Rarity cannot be empty");

        uint256 newNftTypeId = _nftTypeIdCounter;
        _nftTypeIdCounter++;

        nftTypes[newNftTypeId] = RoomCardNftType({
            id: newNftTypeId,
            name: name,
            maxPlayers: maxPlayers,
            price: price,
            uriSuffix: uriSuffix,
            active: true,
            maxMint: maxMint,
            rarity: rarity,
            minted: 0,
            __gap: [uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0)]
        });
        _allNftTypeIds.push(newNftTypeId);
        emit CardTypeAdded(newNftTypeId, name, maxPlayers, price, uriSuffix, maxMint, rarity);
        return newNftTypeId;
    }

    /**
     * @dev Update an existing card type
     * @param nftTypeId Card type ID to update
     * @param maxPlayers New maximum number of players
     * @param price New price
     * @param uriSuffix New URI suffix
     */
    function updateType(
        uint256 nftTypeId,
        uint8 maxPlayers,
        uint256 price,
        string memory uriSuffix,
        uint256 maxMint,
        string memory rarity
    ) external onlyOwner {
        require(nftTypes[nftTypeId].id == nftTypeId, "Card type does not exist");
        require(maxPlayers > 1, "Max players must be greater than 1");
        require(price > 0, "Price must be greater than 0");
        require(maxMint > 0, "Max mint must be greater than 0");
        require(bytes(rarity).length > 0, "Rarity cannot be empty");
        RoomCardNftType storage nftType = nftTypes[nftTypeId];
        nftType.maxPlayers = maxPlayers;
        nftType.price = price;
        nftType.uriSuffix = uriSuffix;
        nftType.maxMint = maxMint;
        nftType.rarity = rarity;
        emit CardTypeUpdated(nftTypeId, maxPlayers, price, uriSuffix, maxMint, rarity);
    }

    /**
     * @dev Set a card type's active status
     * @param nftTypeId Card type ID
     * @param active New active status
     */
    function setTypeActive(uint256 nftTypeId, bool active) external onlyOwner {
        // Verify card type exists
        require(nftTypes[nftTypeId].id == nftTypeId, "Card type does not exist");

        nftTypes[nftTypeId].active = active;
        emit CardTypeActiveStatusChanged(nftTypeId, active);
    }

    /**
     * @dev Purchase a room card
     * @param nftTypeId Card type ID
     * @return Returns the minted room card ID
     */
    function buy(uint256 nftTypeId) external payable returns (uint256) {
        RoomCardNftType storage nftType = nftTypes[nftTypeId];
        require(nftType.id == nftTypeId, "Card type does not exist");
        require(nftType.active, "Card type not active");
        require(msg.value >= nftType.price, "Insufficient payment");
        require(nftType.minted < nftType.maxMint, "Max mint reached for this card type");

        nftType.minted += 1;
        uint256 tokenId = _tokenIdCounter;
        _tokenIdCounter++;
        _safeMint(msg.sender, tokenId);
        tokenNftTypes[tokenId] = nftTypeId;
        if (msg.value > nftType.price) {
            uint256 refundAmount = msg.value - nftType.price;
            (bool success, ) = payable(msg.sender).call{value: refundAmount}("");
            require(success, "Refund failed");
        }
        emit RoomCardPurchased(msg.sender, tokenId, nftType.price, nftTypeId);
        return tokenId;
    }

    /**
     * @dev Batch purchase room cards
     * @param nftTypeId Card type ID
     * @param amount Purchase quantity
     * @return Returns an array of minted room card IDs
     */
    function batchBuy(uint256 nftTypeId, uint256 amount) external payable returns (uint256[] memory) {
        RoomCardNftType storage nftType = nftTypes[nftTypeId];
        require(nftType.id == nftTypeId, "Card type does not exist");
        require(nftType.active, "Card type not active");
        require(amount > 0, "Amount must be greater than 0");
        require(msg.value >= nftType.price * amount, "Insufficient payment");
        require(nftType.minted + amount <= nftType.maxMint, "Max mint reached for this card type");

        uint256[] memory tokenIds = new uint256[](amount);
        for (uint256 i = 0; i < amount; i++) {
            nftType.minted += 1;
            require(nftType.minted <= nftType.maxMint, "Max mint reached for this card type");

            uint256 tokenId = _tokenIdCounter;
            _tokenIdCounter++;
            _safeMint(msg.sender, tokenId);
            tokenIds[i] = tokenId;
            tokenNftTypes[tokenId] = nftTypeId;
        }
        uint256 totalPrice = nftType.price * amount;
        if (msg.value > totalPrice) {
            uint256 refundAmount = msg.value - totalPrice;
            (bool success, ) = payable(msg.sender).call{value: refundAmount}("");
            require(success, "Refund failed");
        }
        emit BatchRoomCardPurchased(msg.sender, tokenIds, totalPrice, nftTypeId);
        return tokenIds;
    }

    /**
     * @dev Consume room card
     * @param owner Owner of the room card
     * @param tokenId Room card ID to be consumed
     */
    function consume(address owner, uint256 tokenId) external onlyGameTable {
        require(_ownerOf(tokenId) == owner, "Not approved or owner");
        _burn(tokenId);

        emit RoomCardConsumed(owner, tokenId);
    }

    function increaseMaxMint(uint256 nftTypeId, uint256 addAmount) external onlyOwner {
        require(nftTypes[nftTypeId].id == nftTypeId, "Card type does not exist");
        require(addAmount > 0, "Add amount must be greater than 0");
        nftTypes[nftTypeId].maxMint += addAmount;
    }

    /**
     * @dev Set base URI
     * @param baseURI New base URI
     */
    function setBaseURI(string memory baseURI) external onlyOwner {
        _baseTokenURI = baseURI;
    }

    /**
     * @dev Override base URI function
     */
    function _baseURI() internal view override returns (string memory) {
        return _baseTokenURI;
    }

    /**
     * @dev Override tokenURI function, returns different URIs based on room card type
     */
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        _requireOwned(tokenId);

        uint256 nftTypeId = tokenNftTypes[tokenId];
        string memory baseURI = _baseURI();
        string memory suffix = nftTypes[nftTypeId].uriSuffix;

        return bytes(baseURI).length > 0 ?
            string(abi.encodePacked(baseURI, suffix)) : "";
    }



    /**
     * @dev Withdraw ETH from the contract
     */
    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds to withdraw");

        (bool success, ) = payable(owner()).call{value: balance}("");
        require(success, "Withdrawal failed");

        emit Withdrawn(owner(), balance);
    }

    /**
     * @dev Check if a user owns any room cards
     * @param owner User address
     * @return Whether the user owns any room cards
     */
    function hasNft(address owner) external view returns (bool) {
        return balanceOf(owner) > 0;
    }


    /**
     * @dev 
     * @param userAddress 
     * @return has 
     * @return details 
     */
    function getUserNfts(address userAddress) external view returns (bool has, RoomCardNftDetail[] memory details) {
        has = balanceOf(userAddress) > 0;

        if (has) {
            uint256 balance = balanceOf(userAddress);
            details = new RoomCardNftDetail[](balance);

            for (uint256 i = 0; i < balance; i++) {
                uint256 tokenId = tokenOfOwnerByIndex(userAddress, i);
                uint256 nftTypeId = tokenNftTypes[tokenId];
                
                details[i] = RoomCardNftDetail({
                    tokenId: tokenId,
                    nftType: nftTypes[nftTypeId]
                });
            }
        } else {
            details = new RoomCardNftDetail[](0);
        }

        return (has, details);
    }

    /**
     * @dev Get card type for a specific token
     * @param tokenId Room card ID
     * @return Card type information
     */
    function getType(uint256 tokenId) external view returns (RoomCardNftType memory) {
        _requireOwned(tokenId);
        uint256 nftTypeId = tokenNftTypes[tokenId];
        return nftTypes[nftTypeId];
    }

    /**
     * @dev Get all card type IDs
     * @return Array of all card type IDs
     */
    function getAllTypeIds() external view returns (uint256[] memory) {
        return _allNftTypeIds;
    }

    /**
     * @dev Get all active card types
     * @return Arrays of card type IDs and card types
     */
    function getActiveTypes() external view returns (uint256[] memory, RoomCardNftType[] memory) {
        uint256 activeCount = 0;

        // Count active card types
        for (uint256 i = 0; i < _allNftTypeIds.length; i++) {
            uint256 typeId = _allNftTypeIds[i];
            if (nftTypes[typeId].active) {
                activeCount++;
            }
        }

        // Create arrays for active card types
        uint256[] memory activeIds = new uint256[](activeCount);
        RoomCardNftType[] memory activeTypes = new RoomCardNftType[](activeCount);

        // Fill arrays
        uint256 index = 0;
        for (uint256 i = 0; i < _allNftTypeIds.length; i++) {
            uint256 typeId = _allNftTypeIds[i];
            if (nftTypes[typeId].active) {
                activeIds[index] = typeId;
                activeTypes[index] = nftTypes[typeId];
                index++;
            }
        }

        return (activeIds, activeTypes);
    }

    /**
     * @dev Validate if room card meets game parameter requirements
     * @param tokenId Room card ID
     * @param maxPlayers Maximum number of players
     * @return Whether requirements are met
     */
    function validateParams(uint256 tokenId, uint8 maxPlayers) external view returns (bool) {
        _requireOwned(tokenId);
        uint256 nftTypeId = tokenNftTypes[tokenId];
        RoomCardNftType memory nftType = nftTypes[nftTypeId];

        return (maxPlayers <= nftType.maxPlayers);
    }

    function _increaseBalance(address account, uint128 value) internal virtual override(ERC721Upgradeable, ERC721EnumerableUpgradeable) {
        super._increaseBalance(account, value);
    }

    function _update(
        address to,
        uint256 tokenId,
        address auth
    ) internal override(ERC721Upgradeable, ERC721EnumerableUpgradeable) returns (address) {
        return super._update(to, tokenId, auth);
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override(ERC721Upgradeable, ERC721EnumerableUpgradeable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    // Authorize upgrade
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    /**
     * @dev Function that must be implemented by contracts that need to receive funds
     */
    receive() external payable {}

    // Event definitions
    event CardTypeAdded(uint256 indexed nftTypeId, string name, uint8 maxPlayers, uint256 price, string uriSuffix, uint256 maxMint, string rarity);
    event CardTypeUpdated(uint256 indexed nftTypeId, uint8 maxPlayers, uint256 price, string uriSuffix, uint256 maxMint, string rarity);
    event CardTypeMaxMintIncreased(uint256 indexed nftTypeId, uint256 newMaxMint);
    event CardTypeActiveStatusChanged(uint256 indexed nftTypeId, bool active);
    event RoomCardPurchased(address indexed buyer, uint256 tokenId, uint256 price, uint256 nftTypeId);
    event BatchRoomCardPurchased(address indexed buyer, uint256[] tokenIds, uint256 totalPrice, uint256 nftTypeId);
    event RoomCardConsumed(address indexed owner, uint256 tokenId);
    event Withdrawn(address indexed to, uint256 amount);
}

contracts/BBRoomLevelNFT.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.28;

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import "./BBStructs.sol";

/**
 * @title BBRoomLevel
 * @dev Niu Niu game room level NFT contract that determines how many rooms a player can create
 */
contract BBRoomLevelNFT is
    Initializable,
    ERC721Upgradeable,
    ERC721EnumerableUpgradeable,
    OwnableUpgradeable,
    UUPSUpgradeable
{
    using Strings for uint256;


    // Used to generate unique token IDs
    uint256 private _tokenIdCounter;

    // Used to generate unique level type IDs
    uint256 private _nftTypeIdCounter;

    // Room level base URI
    string private _baseTokenURI;

    // Game main contract address
    address public gameMainAddress;

    // Level types by ID
    mapping(uint256 => RoomLevelNftType) public nftTypes;

    // Level type ID corresponding to each token ID
    mapping(uint256 => uint256) public tokenNftTypes;

    // Array of all level type IDs
    uint256[] private _allNftTypeIds;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialization function, replaces constructor
     */
    function initialize(
        string memory name,
        string memory symbol,
        string memory baseTokenURI
    ) public initializer {
        __ERC721_init(name, symbol);
        __ERC721Enumerable_init();
        __Ownable_init(msg.sender);
        __UUPSUpgradeable_init();

        _baseTokenURI = baseTokenURI;
        _tokenIdCounter = 0;
        _nftTypeIdCounter = 1; // Start from 1
    }

    /**
     * @dev Set the game main contract address
     * @param _gameMainAddress Game main contract address
     */
    function setGameMainAddress(address _gameMainAddress) external onlyOwner {
        require(_gameMainAddress != address(0), "Invalid game main address");
        gameMainAddress = _gameMainAddress;
    }

    /**
     * @dev Add a new level type
     * @param name Name of the level type
     * @param maxRooms Maximum number of rooms allowed
     * @param price Price to purchase this level
     * @param uriSuffix URI suffix for metadata
     * @return The ID of the newly created level type
     */
    function addType(
        string memory name,
        uint256 maxRooms,
        uint256 price,
        string memory uriSuffix,
        uint256 maxMint,
        string memory rarity
    ) external onlyOwner returns (uint256) {
        require(bytes(name).length > 0, "Name cannot be empty");
        require(maxRooms > 0, "Max rooms must be greater than 0");
        require(price > 0, "Price must be greater than 0");
        require(maxMint > 0, "Max mint must be greater than 0");

        uint256 newNftTypeId = _nftTypeIdCounter;
        _nftTypeIdCounter++;

        nftTypes[newNftTypeId] = RoomLevelNftType({
            id: newNftTypeId,
            name: name,
            maxRooms: maxRooms,
            price: price,
            uriSuffix: uriSuffix,
            active: true,
            maxMint: maxMint,
            minted: 0,
            rarity: rarity,
            __gap: [uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0), uint256(0)]
        });

        _allNftTypeIds.push(newNftTypeId);

        emit LevelTypeAdded(newNftTypeId, name, maxRooms, price, uriSuffix);
        return newNftTypeId;
    }

    /**
     * @dev Update an existing level type
     * @param nftTypeId Level type ID to update
     * @param maxRooms New maximum number of rooms
     * @param price New price
     * @param uriSuffix New URI suffix
     */
    function updateType(
        uint256 nftTypeId,
        uint256 maxRooms,
        uint256 price,
        string memory uriSuffix,
        uint256 maxMint,
        string memory rarity
    ) external onlyOwner {
        require(nftTypes[nftTypeId].id == nftTypeId, "Level type does not exist");
        require(maxRooms > 0, "Max rooms must be greater than 0");
        require(price > 0, "Price must be greater than 0");
        require(maxMint > 0, "Max mint must be greater than 0");

        RoomLevelNftType storage nftType = nftTypes[nftTypeId];
        nftType.maxRooms = maxRooms;
        nftType.price = price;
        nftType.uriSuffix = uriSuffix;
        nftType.maxMint = maxMint;
        nftType.rarity = rarity;

        emit LevelTypeUpdated(nftTypeId, maxRooms, price, uriSuffix);
    }

    /**
     * @dev Set a level type's active status
     * @param nftTypeId Level type ID
     * @param active New active status
     */
    function setTypeActive(uint256 nftTypeId, bool active) external onlyOwner {
        // Verify level type exists
        require(nftTypes[nftTypeId].id == nftTypeId, "Level type does not exist");

        nftTypes[nftTypeId].active = active;
        emit LevelTypeActiveStatusChanged(nftTypeId, active);
    }

    /**
     * @dev Purchase a room level
     * @param nftTypeId Level type ID
     * @return Returns the minted level token ID
     */
    function buy(uint256 nftTypeId) external payable returns (uint256) {
        RoomLevelNftType storage nftType = nftTypes[nftTypeId];
        require(nftType.id == nftTypeId, "Level type does not exist");
        require(nftType.active, "Level type not active");
        require(msg.value >= nftType.price, "Insufficient payment");
        require(nftType.minted < nftType.maxMint, "Max mint reached for this level type");

        nftType.minted += 1;
        uint256 tokenId = _tokenIdCounter;
        _tokenIdCounter++;
        _safeMint(msg.sender, tokenId);
        tokenNftTypes[tokenId] = nftTypeId;

        if (msg.value > nftType.price) {
            uint256 refundAmount = msg.value - nftType.price;
            (bool success, ) = payable(msg.sender).call{value: refundAmount}("");
            require(success, "Refund failed");
        }

        emit RoomLevelPurchased(msg.sender, tokenId, nftType.price, nftTypeId);
        return tokenId;
    }

    /**
     * @dev Batch purchase room levels
     * @param nftTypeId Level type ID
     * @param amount Purchase quantity
     * @return Returns an array of minted room level IDs
     */
    function batchBuy(uint256 nftTypeId, uint256 amount) external payable returns (uint256[] memory) {
        RoomLevelNftType storage nftType = nftTypes[nftTypeId];
        require(nftType.id == nftTypeId, "Level type does not exist");
        require(nftType.active, "Level type not active");
        require(amount > 0, "Amount must be greater than 0");
        require(msg.value >= nftType.price * amount, "Insufficient payment");
        require(nftType.minted + amount <= nftType.maxMint, "Max mint reached for this level type");

        uint256[] memory tokenIds = new uint256[](amount);
        for (uint256 i = 0; i < amount; i++) {
            nftType.minted += 1;
            require(nftType.minted <= nftType.maxMint, "Max mint reached for this level type");

            uint256 tokenId = _tokenIdCounter;
            _tokenIdCounter++;
            _safeMint(msg.sender, tokenId);
            tokenIds[i] = tokenId;
            tokenNftTypes[tokenId] = nftTypeId;
        }

        uint256 totalPrice = nftType.price * amount;
        if (msg.value > totalPrice) {
            uint256 refundAmount = msg.value - totalPrice;
            (bool success, ) = payable(msg.sender).call{value: refundAmount}("");
            require(success, "Refund failed");
        }

        emit BatchRoomLevelPurchased(msg.sender, tokenIds, totalPrice, nftTypeId);
        return tokenIds;
    }

    function increaseMaxMint(uint256 nftTypeId, uint256 addAmount) external onlyOwner {
        require(nftTypes[nftTypeId].id == nftTypeId, "Level type does not exist");
        require(addAmount > 0, "Add amount must be greater than 0");
        nftTypes[nftTypeId].maxMint += addAmount;
    }

    /**
     * @dev Get the maximum number of rooms a user can create
     * @param user User address
     * @return Maximum number of rooms the user can create (sum of all level NFTs owned)
     */
    function getMaxRooms(address user) external view returns (uint256) {
        uint256 balance = balanceOf(user);

        // If user has no level tokens, return default value (0 or 1 depending on your requirements)
        if (balance == 0) {
            return 0; // Default value, can be changed as needed
        }

        // Calculate total max rooms from all level tokens owned by the user
        uint256 totalMaxRooms = 0;

        for (uint256 i = 0; i < balance; i++) {
            uint256 tokenId = tokenOfOwnerByIndex(user, i);
            uint256 nftTypeId = tokenNftTypes[tokenId];

            // Add this token's max rooms to the total
            totalMaxRooms += nftTypes[nftTypeId].maxRooms;
        }

        return totalMaxRooms;
    }

    /**
     * @dev Set base URI
     * @param baseURI New base URI
     */
    function setBaseURI(string memory baseURI) external onlyOwner {
        _baseTokenURI = baseURI;
    }

    /**
     * @dev Override base URI function
     */
    function _baseURI() internal view override returns (string memory) {
        return _baseTokenURI;
    }

    /**
     * @dev Override tokenURI function, returns different URIs based on level type
     */
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        _requireOwned(tokenId);

        uint256 nftTypeId = tokenNftTypes[tokenId];
        string memory baseURI = _baseURI();
        string memory suffix = nftTypes[nftTypeId].uriSuffix;

        return bytes(baseURI).length > 0 ?
            string(abi.encodePacked(baseURI, suffix, "/", tokenId.toString())) : "";
    }

    /**
     * @dev Withdraw ETH from the contract
     */
    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds to withdraw");

        (bool success, ) = payable(owner()).call{value: balance}("");
        require(success, "Withdrawal failed");

        emit Withdrawn(owner(), balance);
    }

    /**
     * @dev Check if a user has a room level
     * @param user User address
     * @return Whether the user has a room level
     */
    function hasNft(address user) external view returns (bool) {
        return balanceOf(user) > 0;
    }

    /**
     * @dev Get user's room level information
     * @param userAddress User address to check
     * @return has Whether the user has any room level
     * @return details Array of level details
     * @return totalMaxRooms Total maximum rooms allowed
     */
    function getUserNfts(address userAddress) external view returns (
        bool has,
        RoomLevelNftDetail[] memory details,
        uint256 totalMaxRooms
    ) {
        has = balanceOf(userAddress) > 0;

        if (has) {
            uint256 balance = balanceOf(userAddress);
            details = new RoomLevelNftDetail[](balance);

            totalMaxRooms = 0;
            for (uint256 i = 0; i < balance; i++) {
                uint256 tokenId = tokenOfOwnerByIndex(userAddress, i);
                uint256 nftTypeId = tokenNftTypes[tokenId];

                details[i] = RoomLevelNftDetail({
                    tokenId: tokenId,
                    nftType: nftTypes[nftTypeId]
                });

                totalMaxRooms += nftTypes[nftTypeId].maxRooms;
            }
        } else {
            details = new RoomLevelNftDetail[](0);
            totalMaxRooms = 0;
        }

        return (has, details, totalMaxRooms);
    }

    /**
     * @dev Get level type for a specific token
     * @param tokenId Level token ID
     * @return Level type information
     */
    function getType(uint256 tokenId) external view returns (RoomLevelNftType memory) {
        _requireOwned(tokenId);
        uint256 nftTypeId = tokenNftTypes[tokenId];
        return nftTypes[nftTypeId];
    }

    /**
     * @dev Get all level type IDs
     * @return Array of all level type IDs
     */
    function getAllTypeIds() external view returns (uint256[] memory) {
        return _allNftTypeIds;
    }

    /**
     * @dev Get all active level types
     * @return Arrays of level type IDs and level types
     */
    function getActiveTypes() external view returns (uint256[] memory, RoomLevelNftType[] memory) {
        uint256 activeCount = 0;

        // Count active level types
        for (uint256 i = 0; i < _allNftTypeIds.length; i++) {
            uint256 typeId = _allNftTypeIds[i];
            if (nftTypes[typeId].active) {
                activeCount++;
            }
        }

        // Create arrays for active level types
        uint256[] memory activeIds = new uint256[](activeCount);
        RoomLevelNftType[] memory activeTypes = new RoomLevelNftType[](activeCount);

        // Fill arrays
        uint256 index = 0;
        for (uint256 i = 0; i < _allNftTypeIds.length; i++) {
            uint256 typeId = _allNftTypeIds[i];
            if (nftTypes[typeId].active) {
                activeIds[index] = typeId;
                activeTypes[index] = nftTypes[typeId];
                index++;
            }
        }

        return (activeIds, activeTypes);
    }

    function _increaseBalance(address account, uint128 value) internal virtual override(ERC721Upgradeable, ERC721EnumerableUpgradeable) {
        super._increaseBalance(account, value);
    }

    /**
     * @dev Override _update to prevent transfers
     */
    function _update(
        address to,
        uint256 tokenId,
        address auth
    ) internal override(ERC721Upgradeable, ERC721EnumerableUpgradeable) returns (address) {
        address from = _ownerOf(tokenId);

        // If this is a transfer (not a mint or burn), prevent it
        if (from != address(0) && to != address(0)) {
            // Only allow transfers initiated by the contract itself
            require(auth == address(this), "BBRoomLevel: NFT is not transferable");
        }

        return super._update(to, tokenId, auth);
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override(ERC721Upgradeable, ERC721EnumerableUpgradeable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    // Authorize upgrade
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    /**
     * @dev Function that must be implemented by contracts that need to receive funds
     */
    receive() external payable {}

    // Event definitions
    event LevelTypeAdded(uint256 indexed nftTypeId, string name, uint256 maxRooms, uint256 price, string uriSuffix);
    event LevelTypeUpdated(uint256 indexed nftTypeId, uint256 maxRooms, uint256 price, string uriSuffix);
    event LevelTypeActiveStatusChanged(uint256 indexed nftTypeId, bool active);
    event RoomLevelPurchased(address indexed buyer, uint256 tokenId, uint256 price, uint256 nftTypeId);
    event BatchRoomLevelPurchased(address indexed buyer, uint256[] tokenIds, uint256 totalPrice, uint256 nftTypeId);
    event RoomLevelUpgraded(address indexed owner, uint256 tokenId, uint256 oldLevelTypeId, uint256 newLevelTypeId, uint256 pricePaid);
    event Withdrawn(address indexed to, uint256 amount);
}

